import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

export const fetchSuggestion =
  createAsyncThunk(/* Task 15: Complete the `createAsyncThunk()` function to load a suggestion from this URL: http://localhost:3004/api/suggestion */

  'suggestion/fetchSuggestion', async () => {
    const response= await fetch("http://localhost:3004/api/suggestion"); 
    const suggestion = await response.json();
    return suggestion;
  });

const initialState = {
  suggestion: {caption:'',imageUrl:''},
  loading: false,
  error: true,
};

const options = {
  name: 'suggestion',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(fetchSuggestion.pending, (state,action)=>{state.suggestion = action.payload;state.loading = true;state.error = false;})
    builder.addCase(fetchSuggestion.fulfilled, (state,action)=>{state.suggestion.caption = action.payload.caption;state.suggestion.imageUrl = action.payload.imageUrl;state.loading = false;state.error = false;})
    builder.addCase(fetchSuggestion.rejected, (state,action)=>{state.suggestion = action.payload;state.loading = false;state.error = action.error.message;})
    /* Task 16: Inside `extraReducers`, add reducers to handle all three promise lifecycle states - pending, fulfilled, and rejected - for the `fetchSuggestion()` call */

  },
};

const suggestionSlice = createSlice(options);

export default suggestionSlice.reducer;

// Task 17: Create a selector, called `selectSuggestion`, for the `suggestion` state variable and export it from the file
export const selectSuggestion = (state) => state.suggestion.suggestion;
export const selectLoading = (state) => state.suggestion.loading;
export const selectError = (state) => state.suggestion.error;
